module.exports = function(grunt) {
    'use strict';

    var chmod = {};

    var fileset = this.learn('initConfig.buildinfo.packagefiles.set_dest_filepermissions') || [];

    var num = 0;
    for (var i = 0; i < fileset.length; i++) {
        var entry = fileset[i];
        num = num + 1;
        chmod['target' + num.toString()] = {
            options: {
                mode: entry.mode
            },
            src: [entry.name]
        }
    }

    return chmod;
}