module.exports = function() {
    'use strict';

    var keep_stage = process.env['KEEP_STAGE'] || 0;
    var after_build_clean = [];
    var before_build_clean = [];

    if (keep_stage == 0) {
        after_build_clean = ['build', 'dist', 'doc', 'stage'];
    }
    before_build_clean = ['stage', '*.<%= buildinfo.extension%>', 'build', 'doc', 'dist'];

    return {
        options: {
            force: true,
        },
        before_build: {
            src: before_build_clean
        },
        after_build: {
            src: after_build_clean
        }
    };
};
