module.exports = function(grunt) {
    'use strict';

    var concat = {};

    var configurations = this.learn('initConfig.buildinfo.packagefiles.configurations') || [];

    var num = 0;

    for (var i = 0; i < configurations.length; i++) {
        var entry = configurations[i];
        var src = [entry.src];

        num = num + 1;

        if (entry.append == 1) {
            src.unshift(entry.dest);
        }
        concat['conf' + num.toString()] = {
            dest: entry.dest,
            src: src
        };
    }


    return concat;
};
