module.exports = function exports(grunt) {
    'use strict';

    grunt.log.subhead("loading npm tasks jit");
    require('jit-grunt')(grunt);

    var fs = require('fs');
    fs.stat('./config/grunt/tasks', function(err, stat) {
        if (err == null) {
            grunt.log.writeln("loading local project tasks");
            grunt.loadTasks('./config/grunt/tasks');
            grunt.log.ok();
        }
    });
};
