/*jslint node: true */
'use strict';

var grunt = require('grunt');

module.exports = function(grunt) {

    // register our main task
    grunt.registerTask('create_appconf_file', "Create App conf",
        function() {

            var retval = true;

            var filename = grunt.config.process('stage/app-<%=package.name%>/default/app.conf');
            var lines = ['[ui]', 'is_visible = 1', 'label = Standalone App'];

            grunt.file.write(filename, lines.join('\n'));

            return retval;
        }
    );
};