/*jslint node: true */
'use strict';

var grunt = require('grunt');

module.exports = function(grunt) {

    // register our main task
    grunt.registerTask('create_webconf_file', "Create Web conf",
        function() {
            var retval = true;
            var filename = grunt.config.process(grunt.config.get('environments').production.deploy_path + '/etc/system/local/web.conf');
            var lines = ['[settings]', 'js_no_cache = True', 'minify_js = false', 'minify_css = true'];
            grunt.file.write(filename, lines.join('\n'));
            return retval;
        }
    );
};